import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory
import axios from 'axios'; // Import axios

const LoginPage = () => {
  const [studentDetails, setStudentDetails] = useState({
    name: '',
    matricule: '',
    major: '',
    specialization: '',
    session: '',
    courseCode: '',
    semester: '',
    consent: false,
    examCode: '' // New field for exam code
  });
  const history = useHistory();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setStudentDetails({
      ...studentDetails,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Save student details to local storage or context
    localStorage.setItem('studentDetails', JSON.stringify(studentDetails));
    history.push('/student');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        placeholder="Name"
        value={studentDetails.name}
        onChange={handleChange}
      />
      {/* Add other input fields for student details */}
      <input
        type="text"
        name="examCode"
        placeholder="Exam Code"
        value={studentDetails.examCode}
        onChange={handleChange}
      />
      <label>
        <input
          type="checkbox"
          name="consent"
          checked={studentDetails.consent}
          onChange={handleChange}
        />
        I consent that the answers are mine.
      </label>
      <button type="submit">Start Exam</button>
    </form>
  );
};

export default LoginPage;
