import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './AdminLoginPage.css';

const AdminLoginPage = () => {
    const [loginDetails, setLoginDetails] = useState({
        name: '',
        adminCode: '',
        password: ''
    });
    const [showSuccess, setShowSuccess] = useState(false); // State to control success message visibility
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setLoginDetails({
            ...loginDetails,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/admin/login', loginDetails);
            localStorage.setItem('token', response.data.token);
            setShowSuccess(true); // Show success message on successful login
            setTimeout(() => {
                setShowSuccess(false); // Hide success message after a certain time
                navigate('/admin');
            }, 2000); // Hide after 2 seconds (adjust as needed)
        } catch (error) {
            if (error.response && error.response.status === 400) {
                console.error('Invalid credentials');
            } else {
                console.error('Login failed:', error);
            }
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2>Login</h2>
                <form onSubmit={handleSubmit} className="login-form">
                    <input
                        type="text"
                        name="name"
                        placeholder="Name"
                        value={loginDetails.name}
                        onChange={handleChange}
                    />
                    <input
                        type="text"
                        name="adminCode"
                        placeholder="Admin Code"
                        value={loginDetails.adminCode}
                        onChange={handleChange}
                    />
                    <input
                        type="password"
                        name="password"
                        placeholder="Password"
                        value={loginDetails.password}
                        onChange={handleChange}
                    />
                    <button type="submit">Login</button>
                    <p>Don't have an account? <Link to="/admin/signup">Sign Up</Link></p>
                </form>
            </div>
            {showSuccess && <div className="success-message">Login successful!</div>} {/* Show success message when showSuccess is true */}
        </div>
    );
};

export default AdminLoginPage;
