import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import AdminPage from './pages/AdminPage';
import StudentPage from './pages/StudentPage';
import LoginPage from './pages/LoginPage';
import AdminLoginPage from './pages/AdminLoginPage';
import AdminSignupPage from './pages/AdminSignupPage';

function App() {
    const token = localStorage.getItem('token'); // Assuming you use localStorage to store the authentication token

    const ProtectedAdminRoute = ({ element }) => {
        return token ? element : <Navigate to="/admin/login" replace />;
    };

    return (
        <Router>
            <Routes>
                <Route path="/admin" element={<ProtectedAdminRoute element={<AdminPage />} />} />
                <Route path="/admin/login" element={<AdminLoginPage />} />
                <Route path="/admin/signup" element={<AdminSignupPage />} />
                <Route path="/student/:examCode" element={<StudentPage />} />  {/* Dynamic route */}
                <Route path="/" element={<LoginPage />} />
            </Routes>
        </Router>
    );
}

export default App;
